import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function App() {
  const [users, setUsers] = useState([]);
  const [license, setLicense] = useState(null);
  const [search, setSearch] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    // Fetch users list
    fetch("http://localhost:5000/api/slack/users.list")
      .then((res) => res.json())
      .then((data) => setUsers(data));

    // Fetch license info
    fetch("http://localhost:5000/api/slack/license.info")
      .then((res) => res.json())
      .then((data) => setLicense(data));
  }, []);

  const handleUserClick = (userId) => {
    // Fetch user info
    fetch(`http://localhost:5000/api/slack/users.info?user=${userId}`)
      .then((res) => res.json())
      .then((data) => setSelectedUser(data));
  };

  const filteredUsers = users.filter(
    (user) =>
      user.name.toLowerCase().includes(search.toLowerCase()) ||
      user.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container py-4">
      <h1 className="mb-4">Slack Mock Dashboard</h1>

      {/* License Info */}
      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">License Info</h5>
          {license && (
            <ul>
              <li>Total Licenses: {license.totalLicenses}</li>
              <li>Used Licenses: {license.usedLicenses}</li>
            </ul>
          )}
        </div>
      </div>

      {/* Search Users */}
      <input
        className="form-control mb-3"
        placeholder="Search users"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* Users List */}
      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">All Users</h5>
          <ul className="list-group">
            {filteredUsers.map((user) => (
              <li
                key={user.id}
                className="list-group-item"
                onClick={() => handleUserClick(user.id)}
                style={{ cursor: "pointer" }}
              >
                {user.name} - {user.email}
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Selected User Info */}
      {selectedUser && (
        <div className="card mb-4">
          <div className="card-body">
            <h5 className="card-title">Selected User Info</h5>
            <ul>
              <li>ID: {selectedUser.id}</li>
              <li>Name: {selectedUser.name}</li>
              <li>Email: {selectedUser.email}</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
