import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function SlackDashboard() {
  const [users, setUsers] = useState([]);
  const [license, setLicense] = useState(null);
  const [search, setSearch] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [isLoadingUsers, setIsLoadingUsers] = useState(true);
  const [isLoadingLicense, setIsLoadingLicense] = useState(true);
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/slack/users.list")
      .then((res) => res.json())
      .then((data) => setUsers(data))
      .finally(() => setIsLoadingUsers(false));

    fetch("http://localhost:5000/api/slack/license.info")
      .then((res) => res.json())
      .then((data) => setLicense(data))
      .finally(() => setIsLoadingLicense(false));
  }, []);

  const handleUserClick = (userId) => {
    fetch(`http://localhost:5000/api/slack/users.info?user=${userId}`)
      .then((res) => res.json())
      .then((data) => setSelectedUser(data))
      .catch((err) => console.error(err));
  };

  const handleAddUser = () => {
    if (!newName || !newEmail) {
      console.error("Name and email are required");
      return;
    }

    fetch("http://localhost:5000/api/slack/user.add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name: newName, email: newEmail }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to add user");
        }
        return res.json();
      })
      .then((data) => {
        setUsers((prev) => [...prev, data]); // Add new user to the list
        setNewName(""); // Clear the form
        setNewEmail("");
      })
      .catch((err) => console.error(err));
  };

  const filteredUsers = users.filter(
    (user) =>
      user.name.toLowerCase().includes(search.toLowerCase()) ||
      user.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container py-4">
      <h1 className="mb-4">Slack Mock Dashboard</h1>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">License Info</h5>
          {isLoadingLicense ? (
            <p>Loading license info...</p>
          ) : license ? (
            <ul>
              <li>Total Licenses: {license.totalLicenses}</li>
              <li>Used Licenses: {license.usedLicenses}</li>
            </ul>
          ) : (
            <p>No license info available</p>
          )}
        </div>
      </div>

      <input
        className="form-control mb-3"
        placeholder="Search users"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Add New User</h5>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Name"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <input
            type="email"
            className="form-control mb-2"
            placeholder="Email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleAddUser}>
            Add User
          </button>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">All Users</h5>
          {isLoadingUsers ? (
            <p>Loading users...</p>
          ) : (
            <ul className="list-group">
              {filteredUsers.map((user) => (
                <li
                  key={user.id}
                  className="list-group-item"
                  onClick={() => handleUserClick(user.id)}
                  style={{ cursor: "pointer" }}
                >
                  {user.name} - {user.email}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {selectedUser && (
        <div className="card mb-4">
          <div className="card-body">
            <h5 className="card-title">Selected User Info</h5>
            <ul>
              <li>ID: {selectedUser.id}</li>
              <li>Name: {selectedUser.name}</li>
              <li>Email: {selectedUser.email}</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
